import React, { useState, useEffect } from 'react';
import { useAppContext } from '../context/AppContext';
import './LogsPage.css';

function LogsPage() {
  const { tasks, isLoadingTasks, taskError, fetchTasks } = useAppContext();
  const [selectedTaskId, setSelectedTaskId] = useState(null);
  const [selectedTask, setSelectedTask] = useState(null);
  const [isLoadingDetails, setIsLoadingDetails] = useState(false);
  const [detailsError, setDetailsError] = useState(null);

  // API base URL from environment or default
  const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000';

  // Fetch details for a specific task
  const fetchTaskDetails = async (taskId) => {
    if (!taskId) return;
    
    setIsLoadingDetails(true);
    try {
      const response = await fetch(`${API_BASE_URL}/api/v1/tasks/${taskId}`);
      if (!response.ok) {
        throw new Error(`Failed to fetch task details for ID: ${taskId}`);
      }
      const data = await response.json();
      setSelectedTask(data);
      setDetailsError(null);
    } catch (err) {
      setDetailsError(err.message);
    } finally {
      setIsLoadingDetails(false);
    }
  };

  // Auto-select the newest task when tasks are loaded or changed
  useEffect(() => {
    if (tasks.length > 0 && !selectedTaskId) {
      setSelectedTaskId(tasks[0].id);
    }
  }, [tasks, selectedTaskId]);

  // Fetch details when a task is selected
  useEffect(() => {
    if (selectedTaskId) {
      fetchTaskDetails(selectedTaskId);
      
      // Set up polling for in-progress tasks
      const pollInterval = setInterval(() => {
        if (selectedTask && selectedTask.status !== 'completed' && selectedTask.status !== 'failed') {
          fetchTaskDetails(selectedTaskId);
        }
      }, 5000);
      
      return () => clearInterval(pollInterval);
    }
  }, [selectedTaskId, selectedTask?.status]);

  const handleTaskSelect = (taskId) => {
    setSelectedTaskId(taskId);
  };

  const formatTimestamp = (timestamp) => {
    if (!timestamp) return 'N/A';
    const date = new Date(timestamp);
    return date.toLocaleString();
  };

  const getStatusClass = (status) => {
    switch (status) {
      case 'running': return 'status-running';
      case 'completed': return 'status-completed';
      case 'failed': return 'status-failed';
      default: return 'status-pending';
    }
  };

  return (
    <div className="logs-page">
      <h2>Task Logs</h2>
      
      <div className="logs-container">
        <div className="task-list">
          <h3>Recent Tasks</h3>
          {isLoadingTasks && tasks.length === 0 ? (
            <div className="loading">Loading tasks...</div>
          ) : tasks.length === 0 ? (
            <p>No tasks found</p>
          ) : (
            <ul>
              {tasks.map((task) => (
                <li 
                  key={task.id} 
                  className={`task-item ${selectedTaskId === task.id ? 'selected' : ''}`}
                  onClick={() => handleTaskSelect(task.id)}
                >
                  <div className="task-summary">
                    <span className={`task-status ${getStatusClass(task.status)}`}>
                      {task.status}
                    </span>
                    <span className="task-type">{task.task_type || 'Task'}</span>
                    <span className="task-time">{formatTimestamp(task.startTime)}</span>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
        
        <div className="task-details">
          <h3>Task Details</h3>
          {taskError && <div className="error-message">{taskError}</div>}
          {detailsError && <div className="error-message">{detailsError}</div>}
          {isLoadingDetails && <div className="loading">Loading details...</div>}
          
          {!isLoadingDetails && selectedTask && (
            <div className="selected-task">
              <div className="task-header">
                <div className="task-id">ID: {selectedTask.task_id}</div>
                <div className={`task-status ${getStatusClass(selectedTask.status)}`}>
                  {selectedTask.status}
                </div>
              </div>
              
              <div className="task-info">
                <div>
                  <strong>Type:</strong> {selectedTask.task_type}
                </div>
                <div>
                  <strong>Started:</strong> {formatTimestamp(selectedTask.start_time)}
                </div>
                {selectedTask.end_time && (
                  <div>
                    <strong>Completed:</strong> {formatTimestamp(selectedTask.end_time)}
                  </div>
                )}
              </div>
              
              <div className="logs-section">
                <h4>Logs</h4>
                <pre className="logs-content">
                  {selectedTask.logs && selectedTask.logs.length > 0
                    ? selectedTask.logs.join('\n')
                    : 'No logs available'
                  }
                </pre>
              </div>
            </div>
          )}
          
          {!isLoadingDetails && !selectedTask && !detailsError && (
            <div className="no-selection">
              Select a task to view details
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default LogsPage;
