import React from 'react';
import {
    BrowserRouter as Router,
    Routes,
    Route,
    Link,
    NavLink // Use NavLink for active styling
} from 'react-router-dom';
import LogsPage from './components/LogsPage';
import './App.css'; // Import App specific styles

// Simple Home component
function HomePage() {
    return (
        <div>
            <h2>Welcome to the Monad Testnet Bot Control Panel</h2>
            <p>Select an option from the navigation bar above.</p>
            {/* You can add more components or links here */}
        </div>
    );
}

// Placeholder for other pages if needed
function PlaceholderPage({ title }) {
    return <h2>{title}</h2>;
}

function App() {
    return (
        <Router>
            <div className="App">
                <nav>
                    <ul>
                        <li>
                            {/* Use NavLink for active class */}
                            <NavLink to="/" className={({ isActive }) => isActive ? 'active' : ''} end>
                                Home
                            </NavLink>
                        </li>
                        <li>
                            <NavLink to="/stake" className={({ isActive }) => isActive ? 'active' : ''}>
                                Stake/Unstake (Placeholder)
                            </NavLink>
                        </li>
                        <li>
                            <NavLink to="/swap" className={({ isActive }) => isActive ? 'active' : ''}>
                                Swap (Placeholder)
                            </NavLink>
                        </li>
                         <li>
                             <NavLink to="/deploy" className={({ isActive }) => isActive ? 'active' : ''}>
                                 Deploy (Placeholder)
                             </NavLink>
                         </li>
                         <li>
                             <NavLink to="/send" className={({ isActive }) => isActive ? 'active' : ''}>
                                 Send (Placeholder)
                             </NavLink>
                         </li>
                        <li>
                            <NavLink to="/logs" className={({ isActive }) => isActive ? 'active' : ''}>
                                Task Logs
                            </NavLink>
                        </li>
                    </ul>
                </nav>

                <div className="page-content">
                    <Routes>
                        <Route path="/" element={<HomePage />} />
                        {/* Add routes for your other bot control components here */}
                        <Route path="/stake" element={<PlaceholderPage title="Stake/Unstake Controls" />} />
                        <Route path="/swap" element={<PlaceholderPage title="Swap Controls" />} />
                        <Route path="/deploy" element={<PlaceholderPage title="Deploy Controls" />} />
                        <Route path="/send" element={<PlaceholderPage title="Send Controls" />} />
                        {/* Route for the LogsPage */}
                        <Route path="/logs" element={<LogsPage />} />
                        {/* Add a catch-all route or 404 page if desired */}
                         <Route path="*" element={<h2>Page Not Found</h2>} />
                    </Routes>
                </div>
            </div>
        </Router>
    );
}

export default App;
