import React from 'react';
import { useAppContext } from '../context/AppContext';
import './WorkflowList.css';

// Mock data for workflows (will be replaced with API data later)
const MOCK_WORKFLOWS = [
  { id: 1, name: 'Workflow Name is Test Ja' },
  { id: 2, name: 'NFT Minter workflow' },
  { id: 3, name: 'Workflow#5' },
  { id: 4, name: 'OOOOOOOOOOOOOOOOOOO' },
];

function WorkflowList() {
  const { workflows, selectedWorkflowId, selectWorkflow } = useAppContext();

  return (
    <div className="workflows-container">
      <div className="workflows-header">
        <h2>Workflows</h2>
      </div>
      
      <div className="workflow-list">
        {workflows.map(workflow => (
          <div 
            key={workflow.id} 
            className={`workflow-item ${selectedWorkflowId === workflow.id ? 'selected' : ''}`}
            onClick={() => selectWorkflow(workflow.id)}
          >
            <div className="workflow-name">{workflow.name}</div>
            <div className="workflow-actions">
              <button 
                className="action-btn edit-btn" 
                title="Edit workflow"
                style={{
                  background: 'transparent',
                  border: 'none',
                  color: '#6c757d',
                  cursor: 'pointer',
                  fontSize: '0.75rem',
                  padding: '3px 6px'
                }}
              >
                Edit
              </button>
              <button 
                className="action-btn clone-btn" 
                title="Clone workflow"
                style={{
                  background: 'transparent',
                  border: 'none',
                  color: '#6c757d',
                  cursor: 'pointer',
                  fontSize: '0.75rem',
                  padding: '3px 6px'
                }}
              >
                Clone
              </button>
              <button 
                className="action-btn delete-btn" 
                title="Delete workflow"
                style={{
                  background: 'transparent',
                  border: 'none',
                  color: '#6c757d',
                  cursor: 'pointer',
                  fontSize: '0.75rem',
                  padding: '3px 6px'
                }}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </div>
      
      <div className="add-workflow">
        <button 
          className="add-workflow-btn"
          style={{
            background: '#8359d4',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            padding: '8px 12px',
            cursor: 'pointer',
            fontSize: '0.85rem',
            fontWeight: '500',
            display: 'flex',
            alignItems: 'center',
            gap: '5px'
          }}
        >
          Create Workflow
        </button>
      </div>
    </div>
  );
}

export default WorkflowList;
