import React from 'react';
import { useAppContext } from '../context/AppContext';
import './WalletList.css';

// Mock data for wallets
const MOCK_WALLETS = [
  { id: 1, address: '0x9996..9099', balance: 9.6399, selected: true },
  { id: 2, address: '0x33b6..b433', balance: 0.0000, selected: true },
  { id: 3, address: '0x696a..cc69', balance: 5.6284, selected: true },
  { id: 4, address: '0x6664..3866', balance: 5.5618, selected: false },
];

function WalletList() {
  const { wallets, toggleWalletSelection } = useAppContext();

  return (
    <div className="wallets-container">
      <div className="wallets-header">
        <h2>Current Wallets</h2>
        <button className="manage-btn">Manage <span>â€º</span></button>
      </div>
      
      <div className="wallet-list">
        <div className="wallet-list-header">
          <div className="wallet-checkbox-header"></div>
          <div className="wallet-address-header">Address</div>
          <div className="wallet-balance-header">Balance</div>
        </div>
        
        {wallets.map(wallet => (
          <div key={wallet.id} className="wallet-item">
            <div className="wallet-checkbox">
              <input 
                type="checkbox" 
                checked={wallet.selected} 
                onChange={() => toggleWalletSelection(wallet.id)}
                id={`wallet-${wallet.id}`}
              />
              <label htmlFor={`wallet-${wallet.id}`}>#{wallet.id}</label>
            </div>
            <div className="wallet-address">{wallet.address}</div>
            <div className={`wallet-balance ${wallet.balance === 0 ? 'zero-balance' : ''}`}>
              {wallet.balance.toFixed(4)}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default WalletList;
