                        <span style={{ marginRight: '10px' }}>
                          {step.type === 'delay' ? 'â±ï¸' :
                           step.type === 'stake' ? 'ðŸ’°' :
                           step.type === 'swap' ? 'ðŸ”„' :
                           step.type === 'deploy' ? 'ðŸš€' :
                           step.type === 'send' ? 'ðŸ“¤' :
                           step.type === 'bebop' || step.type === 'izumi' ? 'ðŸ”„' :
                           step.type === 'lilchogstars' ? 'ðŸŽ¨' :
                           step.type === 'mono' ? 'ðŸ’¸' :
                           step.type === 'rubic' ? 'ðŸ’±' :
                           step.type === 'ambient' ? 'ðŸ’¹' :
                           step.type === 'apriori' ? 'âš¡' :
                           step.type === 'bean' ? 'ðŸŒ±' :
