                          <span style={{ fontSize: '0.9rem' }}>âœï¸</span>
                          Edit
                        </button>
                        <button
                          title="Delete step"
                          onClick={() => handleDeleteStep(step.step_id)}
                          style={{
                            background: 'transparent',
                            color: '#dc3545',
                            border: '1px solid #dc3545',
                            borderRadius: '6px',
                            padding: '6px 12px',
                            cursor: 'pointer',
                            fontSize: '0.85rem',
