                <option value="bima">ðŸ’³ Bima Lend</option>
              </select>
              <button 
                onClick={handleAddStep}
                style={{
                  background: 'linear-gradient(to right, #8359d4, #9b6ee3)',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  padding: '10px 20px',
                  cursor: 'pointer',
                  fontWeight: '600',
                  fontSize: '0.95rem',
                  whiteSpace: 'nowrap',
