import React, { useState } from 'react';
import './WorkflowList.css';

// Mock data for workflows (will be replaced with API data later)
const MOCK_WORKFLOWS = [
  { id: 1, name: 'Workflow Name is Test Ja' },
  { id: 2, name: 'NFT Minter workflow' },
  { id: 3, name: 'Workflow#5' },
  { id: 4, name: 'OOOOOOOOOOOOOOOOOOO' },
];

function WorkflowList() {
  const [workflows, setWorkflows] = useState(MOCK_WORKFLOWS);
  const [selectedWorkflow, setSelectedWorkflow] = useState(2); // Default selected: NFT Minter

  const handleSelectWorkflow = (id) => {
    setSelectedWorkflow(id);
    // This would trigger a state update in parent component to show workflow details
    // For now this just updates the selected state locally
  };

  return (
    <div className="workflows-container">
      <div className="workflows-header">
        <h2>Workflows</h2>
      </div>
      
      <div className="workflow-list">
        {workflows.map(workflow => (
          <div 
            key={workflow.id} 
            className={`workflow-item ${selectedWorkflow === workflow.id ? 'selected' : ''}`}
            onClick={() => handleSelectWorkflow(workflow.id)}
          >
            <div className="workflow-name">{workflow.name}</div>
            <div className="workflow-actions">
              <button className="action-btn edit-btn" title="Edit workflow">
                <span>‚úèÔ∏è</span>
              </button>
              <button className="action-btn clone-btn" title="Clone workflow">
                <span>üîÑ</span>
              </button>
              <button className="action-btn delete-btn" title="Delete workflow">
                <span>üóëÔ∏è</span>
              </button>
            </div>
          </div>
        ))}
      </div>
      
      <div className="add-workflow">
        <button className="add-workflow-btn">
          <span>+</span> Create Workflow
        </button>
      </div>
    </div>
  );
}

export default WorkflowList;
