import React, { useState, useEffect } from 'react';
import { useAppContext } from '../context/AppContext';
import './WorkflowDetail.css';

function WorkflowDetail() {
  const { selectedWorkflow, runWorkflow, tasks, fetchTasks } = useAppContext();
  const [skipOnError, setSkipOnError] = useState(true);
  const [isRunning, setIsRunning] = useState(false);
  const [runResult, setRunResult] = useState(null);
  const [isStopping, setIsStopping] = useState(false);
  const [stopResult, setStopResult] = useState(null);

  // Steps definition based on workflow type
  const getWorkflowSteps = () => {
    if (!selectedWorkflow) return [];

    switch (selectedWorkflow.type) {
      case 'stake':
        return [
          { id: 1, title: 'Logging "Stake/Unstake starting..."', expanded: false },
          { id: 2, title: `Stake using ${selectedWorkflow.contractType} contract`, expanded: false },
          { id: 3, title: 'Wait delay period', expanded: false },
          { id: 4, title: `Unstake from ${selectedWorkflow.contractType} contract`, expanded: false }
        ];
      case 'deploy':
        return [
          { id: 1, title: 'Logging "NFT Minter starting..."', expanded: false },
          { id: 2, title: 'Random sleep between 0 and 120 seconds', expanded: false },
          { id: 3, title: 'Deploy counter contract to network', expanded: false }
        ];
      case 'swap':
        return [
          { id: 1, title: 'Logging "Swap starting..."', expanded: false },
          { id: 2, title: `Swap ${selectedWorkflow.fromToken} to ${selectedWorkflow.toToken}`, expanded: false },
          { id: 3, title: 'Verify swap success', expanded: false }
        ];
      case 'send':
        return [
          { id: 1, title: 'Logging "Token send starting..."', expanded: false },
          { id: 2, title: `Generate ${selectedWorkflow.mode === 'random' ? 'random' : 'specific'} recipient`, expanded: false },
          { id: 3, title: 'Send tokens to recipient', expanded: false }
        ];
      default:
        return [
          { id: 1, title: 'Default step 1', expanded: false },
          { id: 2, title: 'Default step 2', expanded: false }
        ];
    }
  };

  const [workflowSteps, setWorkflowSteps] = useState(getWorkflowSteps());

  // Update steps when selected workflow changes
  React.useEffect(() => {
    setWorkflowSteps(getWorkflowSteps());
    setRunResult(null);
    setStopResult(null);
    setIsStopping(false);
  }, [selectedWorkflow]);

  const toggleStepExpand = (id) => {
    setWorkflowSteps(workflowSteps.map(step => 
      step.id === id ? { ...step, expanded: !step.expanded } : step
    ));
  };

  // Handle run workflow button click
  const handleRunWorkflow = async () => {
    setIsRunning(true);
    setRunResult(null);
    setStopResult(null);

    try {
      const result = await runWorkflow();
      setRunResult(result);

      // If successful, show task ID
      if (result.success) {
        console.log("Workflow started with task ID:", result.data.task_id);
      }
    } catch (error) {
      setRunResult({ 
        success: false, 
        message: `Error running workflow: ${error.message}` 
      });
    } finally {
      setIsRunning(false);
    }
  };

  // Handle Stop Task Button Click
  const handleStopTask = async (taskId) => {
    if (!taskId) return;
    setIsStopping(true);
    setStopResult(null);
    console.log("Requesting stop for task:", taskId);

    try {
      const response = await fetch(`/api/v1/stop-task/${taskId}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.detail || 'Failed to send stop request');
      }

      setStopResult({ success: true, message: data.message || 'Stop request sent successfully.' });
      setTimeout(() => {
          if (fetchTasks) fetchTasks(); 
      }, 1500);

    } catch (error) {
      console.error("Error stopping task:", error);
      setStopResult({ success: false, message: `Error: ${error.message}` });
    } finally {
      setIsStopping(false);
    }
  };

  // Get the latest run for this workflow if any (very basic matching by name - would be better with IDs)
  const getLatestRun = () => {
    if (!selectedWorkflow || !tasks || tasks.length === 0) return null;

    const relatedTasks = tasks.filter(task => 
      task.description && task.description.includes(selectedWorkflow.name)
    );

    if (relatedTasks.length === 0) return null;

    relatedTasks.sort((a, b) => new Date(b.start_time) - new Date(a.start_time));

    return relatedTasks[0];
  };

  const latestRun = getLatestRun();

  const canStopTask = latestRun && ['running', 'pending', 'stopping'].includes(latestRun.status?.toLowerCase());

  return (
    <div className="workflow-detail">
      {selectedWorkflow ? (
        <>
          <div className="workflow-title-bar">
            <h2>{selectedWorkflow.name}</h2>

            <div className="workflow-actions">
              <div className="dropdown">
                <button className="run-mode-btn" disabled={isRunning}>
                  Run Mode <span>‚ñº</span>
                </button>
                {/* Dropdown menu would go here */}
              </div>
              <button className="schedule-btn" disabled={isRunning}>
                <span>üóìÔ∏è</span> Schedule
              </button>
              <button 
                className="run-now-btn" 
                onClick={handleRunWorkflow} 
                disabled={isRunning}
              >
                {isRunning ? 'Running...' : 'Run now'}
              </button>
            </div>
          </div>

          {runResult && (
            <div className={`run-result ${runResult.success ? 'success' : 'error'}`}>
              {runResult.message}
              {runResult.success && runResult.data && (
                <div className="task-id">Task ID: {runResult.data.task_id}</div>
              )}
            </div>
          )}

          {stopResult && (
            <div className={`run-result ${stopResult.success ? 'success' : 'error'}`}>
              {stopResult.message}
            </div>
          )}

          {latestRun && (
            <div className="latest-run-info">
              <div className="latest-run-title">Latest Run:</div>
              <div className="latest-run-details">
                <span className={`status status-${latestRun.status?.toLowerCase()}`}>
                  {latestRun.status}
                </span>
                <span className="latest-run-time">
                  {new Date(latestRun.start_time).toLocaleString()}
                </span>
                {canStopTask && (
                  <button 
                    className="stop-task-btn" 
                    onClick={() => handleStopTask(latestRun.task_id)}
                    disabled={isStopping || latestRun.status?.toLowerCase() === 'stopping'}
                    title={latestRun.status?.toLowerCase() === 'stopping' ? 'Stop already requested' : 'Request task stop'}
                  >
                    {isStopping ? 'Stopping...' : (latestRun.status?.toLowerCase() === 'stopping' ? 'Stopping Requested' : 'Stop Task')}
                  </button>
                )}
              </div>
            </div>
          )}

          <div className="workflow-schedule">
            <div className="schedule-info">
              Execute at 19:22 - 20:22 <span className="repeat-icon">üîÑ</span> every day
              <span className="info-icon" title="Schedule information">‚ìò</span>
            </div>

            <div className="error-handling">
              <input 
                type="checkbox" 
                id="skip-on-error" 
                checked={skipOnError}
                onChange={() => setSkipOnError(!skipOnError)}
              />
              <label htmlFor="skip-on-error">Skip on Error</label>
            </div>
          </div>

          <div className="workflow-steps">
            {workflowSteps.map((step, index) => (
              <div className="workflow-step" key={step.id}>
                <div className="step-header">
                  <div className="step-number">#{index + 1}</div>
                  <div className="step-title">{step.title}</div>
                  <div className="step-actions">
                    <button className="step-action edit-step" title="Edit step">‚úèÔ∏è</button>
                    <button className="step-action delete-step" title="Delete step">üóëÔ∏è</button>
                    <button className="step-action expand-step" onClick={() => toggleStepExpand(step.id)}>
                      {step.expanded ? '‚ñ≤' : '‚ñº'}
                    </button>
                  </div>
                </div>

                {step.expanded && (
                  <div className="step-details">
                    <div className="step-config">
                      <p>Configuration for {step.title}</p>
                      <div className="config-form">
                        {step.id === 1 && (
                          <div className="form-group">
                            <label>Log Message:</label>
                            <input type="text" defaultValue={`Starting ${selectedWorkflow.name}...`} />
                          </div>
                        )}

                        {selectedWorkflow.type === 'stake' && step.id === 2 && (
                          <div className="form-group">
                            <label>Amount to Stake:</label>
                            <input type="text" defaultValue="0.01" />
                            <span className="input-suffix">MON</span>
                          </div>
                        )}

                        {selectedWorkflow.type === 'swap' && step.id === 2 && (
                          <>
                            <div className="form-group">
                              <label>From Token:</label>
                              <input type="text" defaultValue={selectedWorkflow.fromToken || 'ETH'} />
                            </div>
                            <div className="form-group">
                              <label>To Token:</label>
                              <input type="text" defaultValue={selectedWorkflow.toToken || 'USDC'} />
                            </div>
                            <div className="form-group">
                              <label>Amount:</label>
                              <input type="text" defaultValue="0.001" />
                            </div>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                )}
              </div>
            ))}

            <div className="add-step">
              <button className="add-step-btn">
                <span>+</span> Add new task
              </button>
            </div>
          </div>

          <div className="workflow-footer">
            <button className="delete-workflow-btn">
              <span>üóëÔ∏è</span> Delete
            </button>
          </div>
        </>
      ) : (
        <div className="no-workflow-selected">
          <p>No workflow selected. Please select a workflow from the list.</p>
        </div>
      )}
    </div>
  );
}

export default WorkflowDetail;
